// src/translatePlugin.js
export default {
  install(app) {
    app.config.globalProperties.$translate = {
      setLanguage(lang) {
        translate.language.setLocal(lang);
      },
      useService(service) {
        translate.service.use(service);
      },
      execute() {
        translate.execute();
      }
    };
  }
};
