// src/translatePlugin.js
export default {
  install(app) {
    // 确保translate.js库已经加载
    if (typeof translate === 'undefined') {
      console.error('translate.js library is not loaded');
      return;
    }

    app.config.globalProperties.$translate = {
      setLanguage(lang) {
        // eslint-disable-next-line no-undef
        translate.language.setLocal(lang);
      },
      useService(service) {
        // eslint-disable-next-line no-undef
        translate.service.use(service);
      },
      execute() {
        // eslint-disable-next-line no-undef
        translate.execute();
      }
    };
  }
};
