// translateService.js

const { LibreTranslate } = require('libretranslate');

// 创建一个 LibreTranslate 客户端实例
const translate = new LibreTranslate({ endpoint: 'https://libretranslate.com' });

// 导出翻译函数
export async function translateText(text, targetLang) {
  try {
      return await translate.translate(text, 'en', targetLang);
  } catch (error) {
    console.error('Error translating text:', error);
    throw error;
  }
}
