// src/translateService.js
import translate from 'translate';

// 设置翻译源，可以选择'google', 'yandex', 'bing'等
translate.engine = 'google';

// 设置API密钥，如果使用某些翻译引擎可能需要
// translate.key = 'YOUR_API_KEY';

export default async function translateText(text, to = 'en') {
  try {
    return await translate(text, {to});
  } catch (error) {
    console.error('Translation Error:', error);
    return text; // 如果翻译失败，返回原文本
  }
}
