// src/translateService.js
import translate from '@vitalets/google-translate-api';

export default async function translateText(text, targetLanguage = 'en') {
  try {
    const res = await translate(text, { to: targetLanguage });
    return res.text;
  } catch (error) {
    console.error('Translation Error:', error);
    return text; // 如果翻译失败，返回原文本
  }
}
